mapper.select:
SELECT A.* ,(SELECT MCC_S_NAME FROM MF_COMM_CODE WHERE MCC_L_CODE = '100' AND MCC_M_CODE = '104' AND MCC_S_CODE = A.MUK_RCVTYPE) AS MCC_S_NAME ,(SELECT MDI_DPTNAME FROM MF_DPT_INFO WHERE MDI_LINKNO = A.MTI_DPTNO) AS MDI_DPTNAME ,FN_GETCOMCODE_CLS3NAME ('100', '100', MUI_JCCODE) TXT_MUI_JCCODE FROM ( SELECT A.* FROM ( SELECT A.MUK_KJID, A.MUK_RCVTYPE, A.MUK_KJSEQ, A.CMN_DEL_STS, B.MUI_ID, B.MUI_TEAMNO, B.MUI_DPTLVLNAME, B.MUI_DPTLVLCODE, B.MUI_DPTPOSITCODE, B.MUI_DPTPOSITNAME, B.MUI_NAME, B.MUI_LINCNO, B.MUI_JCCODE, (SELECT MTI_DPTNO FROM MF_TEAM_INFO WHERE MTI_TEAMNO = B.MUI_TEAMNO) AS MTI_DPTNO FROM MF_USER_KJP A, MF_USER_INFO B WHERE A.MUK_USERID = B.MUI_ID AND A.CMN_DEL_STS = 'N'
AND B.MUI_TEAMNO = #{sch_teamno}
AND B.MUI_ID = #{sch_mui_id}
) A
WHERE MTI_DPTNO = #{sch_dptno}
ORDER BY A.MUK_RCVTYPE ASC, A.MUK_KJSEQ ASC ) A

mapper.update:
DELETE FROM MF_USER_KJP
WHERE MUK_USERID = #{muk_userid} 
AND MUK_KJID = #{muk_kjid}
