mapper.select:
SELECT MDI_CLSCODE, MDI_DRUGCODE_SEQ, MDI_NAME, MDI_DRUGCODE, (select NVL(MAX(MDI_DRUGCODE_SEQ)+1,'1') from mf_DRUG_INFO where MDI_CLSCODE = #{MDI_CLSCODE}) as seqNumber FROM MF_DRUG_INFO WHERE MDI_DRUGCODE_SEQ !=0 AND CMN_DEL_STS='N' AND MDI_CLSCODE = #{MDI_CLSCODE}
AND MDI_NAME LIKE '%' || #{schKeyword} || '%'
order by MDI_DRUGCODE_SEQ
