mapper.sql:
SELECT TO_CHAR (MAI_DETHDATE, 'YYYY-MM-DD') TXT_MAI_DETHDATE, FN_GETANICLSNAME (MAI_ANICODE, '1') TXT_MAI_ANICODE_1, MAI_VVRMNO, MAC_NAME, MAI_ANICODE, PBR_WEIGHT, PBR_BUDZZNAME, FN_GETCOMCODE_CLS3NAME ('200', '202', MAI_SEX) TXT_MAI_SEX, TO_CHAR (MAI_BIRTHDAY, 'YYYY-MM-DD') TXT_MAI_BIRTHDAY, MAI_DETHPLC, FN_GETCOMCODE_CLS3NAME ('200', '205', MAC_RANK) TXT_MAC_RANK, FN_GETCOMCODE_CLS3NAME ('300', '304', PBR_DZZTYPE) TXT_PBR_DZZTYPE, FN_GETCOMCODE_CLS3NAME ('200', '203', MAI_DETHBDYPRC) TXT_MAI_DETHBDYPRC, FN_GETCOMCODE_CLS3NAME ('200', '206', MAC_CITES) TXT_MAC_CITES, FN_GETCOMCODE_CLS3NAME ('200', '207', MAC_ENDANG) TXT_MAC_ENDANG, MAC_NATMON FROM MF_ANI_CLS, MF_ANI_INFO A LEFT JOIN PS_DETH_REPT ON (MAI_ANICODE = PDR_ANICODE) LEFT JOIN PS_BUKM_REPT ON (PDR_RSLTDOCNO = PBR_DOCNO) WHERE MAI_ANICLSCODE = MAC_ANICLSCODE AND MAI_DETHTYPE = '2'
AND TO_CHAR (MAI_DETHDATE, 'YYYY') = #{schYear}
AND TO_NUMBER(TO_CHAR (MAI_DETHDATE, 'MM')) = #{schMonth}
AND A.CMN_DEL_STS = 'N'
