mapper.select:
SELECT rownum as SEQ, e.* FROM ( select JJR_DocNo, JJJ_DocNo, to_char(JJR_RCDate,'yyyy-mm-dd') as JJR_RCDate, to_char(nvl(JJJ_SDate, JJR_RCDate), 'yyyy-mm-dd') as JJJ_SDate, nvl(JJJ_JRType, '') as JJJ_JRType, MAI_TeamNo, fn_getDptName(MAI_TeamNo) as DptName, MAI_VvrmNo, fn_getVvrmName(MAI_VvrmNo) as VvrmName, BAA_AniCode, MAC_Name, MAI_BName, MAI_Sex, fn_getComCode_Cls3Name(200, 202, MAI_Sex) as SexName, nvl(JJJ_JRDzzName, ' ') as JJJ_JRDzzName, nvl(JJJ_JRCmnt, ' ') as JJJ_JRCmnt, fn_getComCode_Cls3Name('300', '302', nvl(JJJ_JRSts, ' ')) as JRStsName, JJR_Cmnt, JJR_JRType, fn_getComCode_Cls3Name('300', '300', nvl(JJR_JRType, ' ')) as JrTypeName, JJJ_JRDzzType, JJJ_Insu, JJJ_ReqOrga, nvl(JJJ_EDate, JJR_RCDate) as JJJ_EDate, JJJ_JRSts, JJJ_SUserID, JJJ_EUserID, fn_getComCode_Cls3Name('300', '303', nvl(JJJ_JRDzzType, ' ')) as JRDzzTypeName FROM JN_JINRYO_RC left join JN_JINRYO_JNL on JJJ_RCNo = JJR_DocNo, MF_ANI_CLS, MF_ANI_INFO, BZ_ANI_ATCH, MF_VVRM_INFO where MAI_AniClsCode = MAC_AniClsCode AND MAI_AniCode = BAA_AniCode AND JJR_DocNo = BAA_DocNo AND MAI_VvrmNo = MVI_VvrmNo and (JJR_JRNo is null or ( 1=1
AND JJJ_JRType = #{jinryoKind}
AND JJJ_JRSts = #{jinryoStatus}
AND MAC_Name LIKE '%' || #{nameKeyword} || '%'
AND JJJ_DocNo like '%' || #{numKeyword} || '%'
AND MVI_VVRMNAME LIKE '%' || #{vvrmKeyword} || '%'
AND JJJ_SDate >= to_date(#{cal_1},'yyyy-mm-dd')
AND JJJ_SDate <= to_date(#{cal_2} || '23:59:59','YYYY-MM-DD hh24:mi:ss')
)) order by JJR_RCDate, JJJ_DocNo ) e

select count(*) cnt
  from JN_JINRYO_DATE
 where JJD_DocNo=#{JJJ_DOCNO}
   and to_char(JJD_JRDate, 'yyyy-mm-dd') = #{DTLDay}

		select BDA_DOCNO, BDA_BZCLS1, BDA_BZCLS2, BDA_BZCLS3, BDA_BZSEQNO, BDA_BZDATE, BDA_CNTNTTYPE, BDA_ATCHFILE, BDA_CMNT,
       			case when BDA_UPPATH like 'AddDoc%' then UploadPath || 'AddDoc/'
       			     when not BDA_UPPATH like '%AddDoc%' then BDA_UPPATH || 'AddDoc/' 
            		 else BDA_UPPATH end as BDA_UPPATH
  		  from (SELECT BDA_DOCNO, BDA_BZCLS1, BDA_BZCLS2, BDA_BZCLS3, BDA_BZSEQNO, BDA_BZDATE, BDA_CNTNTTYPE, BDA_ATCHFILE, BDA_CMNT,    
                CASE when BDA_UPPATH like '%/' or BDA_UPPATH like '%\' then BDA_UPPATH else BDA_UPPATH || '/' end as BDA_UPPATH 
                FROM BZ_DATA_ATCH),
      		   (SELECT CASE when MSC_UploadPath like '%/' or MSC_UploadPath like '%\' then MSC_UploadPath else MSC_UploadPath || '/' end as UploadPath
      		    FROM MF_SYS_CTRL WHERE MSC_SeqNo = '1')
		 where BDA_DocNo = #{DOCNO}
		
		    AND TO_CHAR(BDA_BzDate,'YYYY-MM-DD') = #{DTLDay}
		
		
			AND BDA_BzCls1 = #{ClsType}

SELECT rownum as SEQ, e.* FROM ( select JJR_DocNo, JJJ_DocNo, to_char(JJR_RCDate,'yyyy-mm-dd') as JJR_RCDate, to_char(nvl(JJJ_SDate, JJR_RCDate), 'yyyy-mm-dd') as JJJ_SDate, to_char(nvl(JJJ_EDate, JJR_RCDate), 'yyyy-mm-dd') as JJJ_EDate, nvl(JJJ_JRType, '') as JJJ_JRType, MAI_TeamNo, fn_getDptName(MAI_TeamNo) as DptName, MAI_VvrmNo, fn_getVvrmName(MAI_VvrmNo) as VvrmName, BAA_AniCode, MAC_Name, MAI_BName, MAI_Sex, fn_getComCode_Cls3Name(200, 202, MAI_Sex) as SexName, nvl(JJJ_JRDzzName, ' ') as JJJ_JRDzzName, nvl(JJJ_JRCmnt, ' ') as JJJ_JRCmnt, fn_getComCode_Cls3Name('300', '302', nvl(JJJ_JRSts, ' ')) as JRStsName, JJR_Cmnt, JJR_JRType, fn_getComCode_Cls3Name('300', '300', nvl(JJR_JRType, ' ')) as JrTypeName, JJJ_JRDzzType, JJJ_Insu, JJJ_ReqOrga, nvl(JJJ_JRSts,'1') as JJJ_JRSts, JJJ_SUserID, JJJ_EUserID, fn_getComCode_Cls3Name('300', '303', nvl(JJJ_JRDzzType, ' ')) as JRDzzTypeName FROM JN_JINRYO_RC left join JN_JINRYO_JNL on JJJ_RCNo = JJR_DocNo, MF_ANI_CLS, MF_ANI_INFO, BZ_ANI_ATCH, MF_VVRM_INFO where MAI_AniClsCode = MAC_AniClsCode AND MAI_AniCode = BAA_AniCode AND JJR_DocNo = BAA_DocNo AND MAI_VvrmNo = MVI_VvrmNo AND JJR_DocNo = #{DOCNO}
AND JJJ_DocNo = #{JJJ_DOCNO}
order by JJR_RCDate, JJJ_DocNo ) e

mapper.update:
 update JN_JINRYO_JNL set JJJ_SDate = to_date(#{SDATE},'yyyy-mm-dd'), JJJ_EDate = to_date(#{EDATE},'yyyy-mm-dd'), 
 						  JJJ_JRType = #{JRTYPE}, JJJ_RCNo = #{DOCNO}, JJJ_JRDzzName = #{JRDZZNAME}, 
 						  JJJ_JRCmnt = #{JRCMNT}, JJJ_JRDzzType = #{JRDZZTYPE}, JJJ_JRSts = #{JRSTS},
 						  JJJ_Insu = #{INSU}, JJJ_ReqOrga = #{REQORGA},
 						  CMN_DEL_STS='N', CMN_UPD_DATE=SYSDATE, CMN_UPD_PROG='TREAT0403', CMN_UPD_ID=#{USERID}
					where JJJ_DocNo = #{JJJ_DOCNO}

mapper.delete:
delete from BZ_DATA_ATCH where BDA_DocNo = #{JJJ_DOCNO} 

	AND TO_CHAR(BDA_BzDate,'YYYY-MM-DD') = #{DTLDay}


	AND BDA_BzCls1 = #{ClsType}
