mapper.select:
SELECT A.* ,(SELECT MUI_NAME FROM MF_USER_INFO WHERE MUI_ID = A.MAG_ID) AS MUI_NAME ,FN_GETDPTNAME(MUI_TEAMNO) AS DPTNAME ,FN_GETCOMCODE_CLS3NAME('100','120',A.MAG_GRPCODE) AS TXT_MAG_GRPCODE ,SUBSTR(MAG_ST_DAY,0,4) || '-' || SUBSTR(MAG_ST_DAY,5,2) || '-' || SUBSTR(MAG_ST_DAY,7,2) TXT_MAG_ST_DAY ,SUBSTR(MAG_ED_DAY,0,4) || '-' || SUBSTR(MAG_ED_DAY,5,2) || '-' || SUBSTR(MAG_ED_DAY,7,2) TXT_MAG_ED_DAY ,LAG(MAG_GRPCODE) OVER (ORDER BY MAG_GRPCODE, FN_GETDPTNAME(MUI_TEAMNO) ) PREV_MAG_GRPCODE ,COUNT(MAG_GRPCODE) OVER (PARTITION BY MAG_GRPCODE ) CNT_MAG_GRPCODE FROM ( SELECT MAG_ID, MAG_GRPCODE, MAG_ST_DAY, MAG_ED_DAY, CMN_DEL_STS, CMN_MAK_DATE, CMN_MAK_PROG, CMN_MAK_ID, CMN_UPD_DATE, CMN_UPD_PROG, CMN_UPD_ID ,(SELECT MUI_TEAMNO FROM MF_USER_INFO WHERE MUI_ID = A.MAG_ID) AS MUI_TEAMNO FROM MF_AUTH_GRP A WHERE CMN_DEL_STS = 'N' AND MAG_GRPCODE NOT IN ('SA','MU')
AND MAG_GRPCODE = #{sch_grpcode}
) A ORDER BY MAG_GRPCODE, DPTNAME

mapper.update:
UPDATE MF_AUTH_GRP
SET MAG_ST_DAY = #{mag_st_day}
    , MAG_ED_DAY = #{mag_ed_day}
    , CMN_UPD_PROG = 'saveAuthGrpList'
    , CMN_UPD_ID = #{ss_userid}
    , CMN_UPD_DATE = SYSDATE
WHERE MAG_ID = #{mag_id}
AND MAG_GRPCODE = #{mag_grpcode}
