mapper.select:
SELECT MAI_AniCode, MAI_AniClsCode, MAC_Name, fn_getComCode_Cls3Name(200, 211, MAI_KnjpType) as MAI_KnjpType, MAI_Sex, SexName, MAI_CurSts, MAI_OSRegDate, DAIO_IOType, DAIO_Resn, DAIO_IODate, DAIO_BoYukSts, DAIO_RentEDate from (select MAI_AniCode, MAI_AniClsCode, MAC_Name, MAI_KnjpType, MAI_Sex, fn_getComCode_Cls3Name(200, 202, MAI_Sex) as SexName, MAI_CurSts, MAI_OSRegDate, DT_ANI_IO.DAIO_IOType, DT_ANI_IO.DAIO_Resn, DT_ANI_IO.DAIO_IODate, DT_ANI_IO.DAIO_BoYukSts, DT_ANI_IO.DAIO_RentEDate FROM MF_ANI_CLS, MF_ANI_INFO, DT_ANI_IO, (select DAIO_AniCode, max(DAIO_IODate) as DAIO_IODate
FROM DT_ANI_IO where TO_CHAR(DAIO_IODate,'YYYY-MM-DD') < #{sdj_jnldate}
AND CMN_DEL_STS = 'N' GROUP BY DAIO_AniCode ) IO WHERE MAI_AniClsCode = MAC_AniClsCode AND IO.DAIO_AniCode = MAI_AniCode AND IO.DAIO_AniCode = DT_ANI_IO.DAIO_AniCode AND IO.DAIO_IODate = DT_ANI_IO.DAIO_IODate AND MAI_VvrmNo = #{mai_vvrmno} UNION SELECT MAI_AniCode, MAI_AniClsCode, MAC_Name, MAI_KnjpType, MAI_Sex, fn_getComCode_Cls3Name(200, 202, MAI_Sex) as SexName, MAI_CurSts, MAI_OSRegDate, DT_ANI_IO.DAIO_IOType, DT_ANI_IO.DAIO_Resn, DT_ANI_IO.DAIO_IODate, DT_ANI_IO.DAIO_BoYukSts, DT_ANI_IO.DAIO_RentEDate FROM MF_ANI_CLS, MF_ANI_INFO, DT_ANI_IO, ( select DAIO_AniCode, max(DAIO_IODate) as DAIO_IODate
FROM DT_ANI_IO WHERE TO_CHAR(DAIO_IODate,'YYYY-MM-DD') < #{sdj_jnldate}
AND CMN_DEL_STS = 'N' GROUP BY DAIO_AniCode ) IO WHERE MAI_AniClsCode = MAC_AniClsCode AND IO.DAIO_AniCode = MAI_AniCode AND IO.DAIO_AniCode = DT_ANI_IO.DAIO_AniCode AND IO.DAIO_IODate = DT_ANI_IO.DAIO_IODate AND MAI_VvrmNo = #{mai_vvrmno} ) ORDER BY MAC_Name, MAI_AniCode, DAIO_IODate

SELECT SDJ01_Cmnt 
FROM SY_DAILY_JNL01 
WHERE SDJ01_DocNo = #{sdj_docno}

	SELECT SDJ05_CMNTETC, SDJ05_RSLTETC, SDJ05_ETCETC
    FROM sy_daily_jnl05
    WHERE SDJ05_DOCNO = #{sdj_docno}

 SELECT Rownum as rnum, DAIO_AniCode, fn_getComCode_Cls3Name(200, 250, DAIO_IOType) as DAIO_IOType, DAIO_Resn, TO_CHAR(DAIO_IODate,'YYYY-MM-DD') as DAIO_IODate,
 TO_CHAR(DAIO_RentEDate,'YYYY-MM-DD') as DAIO_RentEDate, DAIO_Place, DAIO_Cmnt, MAC_Name, MAI_BName    
 FROM DT_ANI_IO, MF_ANI_INFO, MF_ANI_CLS  
 WHERE DAIO_AniCode = MAI_AniCode    
 AND MAI_VvrmNo = #{mai_vvrmno}

 AND TO_CHAR(DAIO_IODate,'YYYY-MM-DD')  <= #{sdj_jnldate}
  
 
 AND TO_CHAR(DAIO_RentEDate,'YYYY-MM-DD') >= #{sdj_jnldate}
 
 AND ( (DAIO_IOType = '2' and DAIO_Resn = '8') or (DAIO_IOType = '1' and DAIO_Resn = '6') )   
 AND MAC_AniClsCode  = MAI_AniClsCode  
 ORDER BY rnum, DAIO_AniCode
