mapper.insert:
INSERT INTO BZ_IMG_INFO ( BII_DOCNO, BII_BZCLS1, BII_BZCLS2, BII_BZCLS3, BII_BZSEQNO, BII_BZDATE, BII_IMGTYPE, BII_ANICODE, BII_IMGFILENM, BII_IMGFILETTL, BII_RPSNTTYPE, CMN_DEL_STS, CMN_MAK_ID, CMN_UPD_ID, CMN_MAK_DATE, CMN_UPD_DATE) SELECT #{bii_docno}, BII_BZCLS1, BII_BZCLS2, BII_BZCLS3, BII_BZSEQNO, BII_BZDATE, BII_IMGTYPE, BII_ANICODE, BII_IMGFILENM , DECODE(BII_BZSEQNO, 1, #{imgfilettl_1}, 2, #{imgfilettl_2}, 3, #{imgfilettl_3}, 4, #{imgfilettl_4}, 5, #{imgfilettl_5}) , DECODE(#{rpsnttype_chkId}, BII_BZSEQNO, '1'), 'N', CMN_MAK_ID, #{ss_userid}, CMN_MAK_DATE, SYSDATE FROM ( SELECT MAX(BII_DOCNO) OVER (ORDER BY BII_DOCNO DESC) MAX_BII_DOCNO, BII_DOCNO, BII_BZCLS1, BII_BZCLS2, BII_BZCLS3, BII_BZSEQNO, BII_BZDATE, BII_IMGTYPE, BII_ANICODE, BII_IMGFILENM, BII_IMGFILETTL, BII_RPSNTTYPE, CMN_MAK_ID, CMN_MAK_DATE FROM BZ_IMG_INFO WHERE BII_BZCLS1 = 'ST' AND BII_ANICODE = #{bii_anicode} AND CMN_DEL_STS = 'N'
AND BII_BZSEQNO NOT IN
#{item}
) A WHERE A.BII_DOCNO = MAX_BII_DOCNO

mapper.update:
MERGE INTO BZ_IMG_INFO II USING ( SELECT #{bii_docno} AS BII_DOCNO, #{bii_bzseqno} AS BII_BZSEQNO FROM DUAL ) SY ON ( II.BII_BZCLS1 = 'SY' AND II.BII_BZCLS2 = 'SY002007' AND II.CMN_DEL_STS = 'N' AND II.BII_DOCNO = SY.BII_DOCNO AND II.BII_BZSEQNO=SY.BII_BZSEQNO ) WHEN MATCHED THEN UPDATE SET CMN_UPD_PROG = 'createSyImgList' , CMN_UPD_ID = #{ss_userid} , CMN_UPD_DATE = SYSDATE
, BII_IMGTYPE = #{bii_imgtype} , BII_IMGFILENM = #{bii_imgfilenm}
, BII_IMGFILETTL = #{bii_imgfilettl} WHEN NOT MATCHED THEN INSERT ( BII_DOCNO, BII_BZCLS1, BII_BZCLS2, BII_BZCLS3, BII_BZSEQNO, BII_BZDATE, BII_IMGTYPE, BII_IMGFILENM,BII_IMGFILETTL , CMN_DEL_STS, CMN_MAK_ID, CMN_MAK_DATE) VALUES (SY.BII_DOCNO,'SY','SY002007','N',SY.BII_BZSEQNO,SYSDATE ,#{bii_imgtype},#{bii_imgfilenm},#{bii_imgfilettl} ,'N',#{ss_userid},SYSDATE)

MERGE INTO BZ_IMG_INFO II USING ( SELECT #{bii_docno} AS BII_DOCNO, #{bii_bzseqno} AS BII_BZSEQNO FROM DUAL ) SS ON ( II.BII_BZCLS1 = 'SS' AND II.BII_BZCLS2 = 'SY003004' AND II.CMN_DEL_STS = 'N' AND II.BII_DOCNO = SS.BII_DOCNO AND II.BII_BZSEQNO = SS.BII_BZSEQNO ) WHEN MATCHED THEN UPDATE SET CMN_UPD_PROG = 'createSyImgList' , CMN_UPD_ID = #{ss_userid} , CMN_UPD_DATE = SYSDATE
, BII_IMGTYPE = #{bii_imgtype} , BII_IMGFILENM = #{bii_imgfilenm}
, BII_IMGFILETTL = #{bii_imgfilettl} WHEN NOT MATCHED THEN INSERT ( BII_DOCNO, BII_BZCLS1, BII_BZCLS2, BII_BZCLS3, BII_BZSEQNO, BII_BZDATE, BII_IMGTYPE, BII_IMGFILENM,BII_IMGFILETTL , CMN_DEL_STS, CMN_MAK_ID, CMN_MAK_DATE) VALUES (SS.BII_DOCNO,'SS','SY003004','N',SS.BII_BZSEQNO,SYSDATE ,#{bii_imgtype},#{bii_imgfilenm},#{bii_imgfilettl} ,'N',#{ss_userid},SYSDATE)
